import React from "react";
import Sidebar from "react-sidebar";



class SidebarWrapper extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      sidebarOpen: true,
      shoppingList : 'basic', // can also be 'hovered'

    }
    this.onSetSidebarOpen = this.onSetSidebarOpen.bind(this);
    this.toggleHover = this.toggleHover.bind(this)
    this.toggleLeave = this.toggleLeave.bind(this)
  }

  onSetSidebarOpen(open) {
    this.setState({ sidebarOpen: open });
  }


  toggleHover(event){

    this.setState({[event.target.getAttribute('name')] : 'hovered' }) 

  }

  toggleLeave(event){
    this.setState({[event.target.getAttribute('name')] : 'basic' })

  }



  render() {
    const shoppingListStyle = this.state.shoppingList === 'hovered' ?  {fontSize: '140%', marginTop: '40px', marginLeft: '30px', color: '#42bcf4', fontFamily : "Brush Script MT"} :  {marginTop: '40px', marginLeft: '30px', color : 'grey'} 


    return (
      <Sidebar
        sidebar={

        <div style = {{display: 'flex', flexDirection: 'column'}}>

                <nav className="navbar navbar-dark bg-dark">
                    <a className="navbar-brand" href="#">
                        <h4 style = {{fontFamily : "Brush Script MT", fontSize: '175%', color: '#42bcf4'}}>Alexa Options</h4>
                    </a>
                </nav>


                <div style = {{display: 'flex', flexDirection: 'column', }}>
                    {/* On hover print 3 >>> */}
                    <strong name = 'shoppingList' style = {shoppingListStyle} onMouseEnter={this.toggleHover} onMouseLeave={this.toggleLeave} >Shopping List &#10146; {this.state.sideBarTitleArrow} </strong>
                    {/* <strong name = 'reminders' style = {remindersStyle}>Reminders &#10146;</strong>
                    <strong name = 'timers'style = {timersStyle}>Timers &#10095;</strong>
                    <strong name = 'alarms' style = {alarmsStyle}>Alarms &#10095;</strong>
                    <strong name = 'Camera' style = {CameraStyle}>Camera &#10095;</strong> */}



                </div>


            </div>


    }
        open={this.state.sidebarOpen}
        onSetOpen={this.onSetSidebarOpen}
        styles={{ sidebar: { background: "white" } }}
      >
        <button className = 'btn btn-link ' onClick={() => this.onSetSidebarOpen(true)}>
        <img style = {{width: '75px', width: '75px'}}src = 'https://png.pngtree.com/svg/20170919/_raspberry_pi_1342096.png'  />  
        </button>

      </Sidebar>
    );
  }
}

export default SidebarWrapper;
