import React from "react";
import Sidebar from "react-sidebar";



class SidebarWrapper extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      sidebarOpen: true,
      sideBarTitleStyle : {marginTop: '40px', marginLeft: '5px', color: 'red'},
      sideBarTitleArrow : '&#10095;',
    }
    this.onSetSidebarOpen = this.onSetSidebarOpen.bind(this);
  }

  onSetSidebarOpen(open) {
    this.setState({ sidebarOpen: open });
  }



  render() {
    return (
      <Sidebar
        sidebar={

        <div style = {{display: 'flex', flexDirection: 'column'}}>

                <nav className="navbar navbar-dark bg-dark">
                    <a className="navbar-brand" href="#">
                        <h4 style = {{fontFamily : "Brush Script MT", fontSize: '175%', color: '#42bcf4'}}>Alexa Options</h4>
                    </a>
                </nav>


                <div style = {{display: 'flex', flexDirection: 'column', }}>
                    {/* On hover print 3 >>> */}
                    <strong style = {this.state.sideBarTitleStyle} >Shopping List {this.state.sideBarTitleArrow} </strong>
                    {/* <strong style = {sidebarTitleStyle}>Reminders &#10146;</strong>
                    <strong style = {sidebarTitleStyle}>Timers &#10095;</strong>
                    <strong style = {sidebarTitleStyle}>Alarms &#10095;</strong>
                    <strong style = {sidebarTitleStyle}>Camera &#10095;</strong> */}



                </div>


            </div>


    }
        open={this.state.sidebarOpen}
        onSetOpen={this.onSetSidebarOpen}
        styles={{ sidebar: { background: "white" } }}
      >
        <button className = 'btn btn-link ' onClick={() => this.onSetSidebarOpen(true)}>
        <img style = {{width: '75px', width: '75px'}}src = 'https://png.pngtree.com/svg/20170919/_raspberry_pi_1342096.png'  />  
        </button>

      </Sidebar>
    );
  }
}

export default SidebarWrapper;
